    });

  let Levels = {
    'Fantastisch proper': "#079cdf",
    'Heel goed': '#84d9ef',
    'Ai, dat kan beter': "#edc8bb",
    'Vuil': "#e38d75"
  };

  let styleCache = {}; // a javascript object literal to cache style
  // the style function returns an array of styles
  // for the given feature and resolution.
  // Return null to hide the feature.
  function styleFunction(feature, resolution) {
    // get the incomeLevel from the feature properties
    let level = feature.get( "klasse");
    // if there is no level or its one we don't recognize,
    // return the default style (in an array!)
    if (!level || !Levels[level]) {
      return [defaultStyle];
    }
    // check the cache and create a new style for the income
    // level if its not been created before.
    if (!styleCache[level]) {
      styleCache[level] = new Style({
        image: new Circle({
          radius: 8,
          fill: new Fill({
            color: Levels[level],
          }),
          stroke: new Stroke({
            color: "white",
            width: 1
          }),
        }),
    });
    }
    // at this point, the style for the current level is in the cache
    // so return it (as an array!)
    return [styleCache[level]];
  }

  // Popup overlay
  const popup = new Popup ({
    popupClass: "default",
    closeBox: true,
    positioning: 'auto',
    autoPan: {
