    }
    else if (klasse == "Ai, dat kan beter") {
      content += `<div class="badge badge-warning rounded-lg mb-2">${klasse}</div>`
    }
    else if (klasse == "Heel goed" ) {
      content += `<div class="badge badge-success rounded-lg mb-2">${klasse}</div>`
    }
    else if (klasse == "Fantastisch proper") {
