  select.getFeatures().on(['remove'], function(e) {
    popup.hide();
  })

  // custom tile layer
  const parser = new WMTSCapabilities();

  onMount(() => {
    if (browser) {
      const view = new View({center: fromLonLat([4.2, 51.]), zoom: 9});

      //fetch('https://inspirepub.waterinfo.be/arcgis/rest/services/klimaatportaal/achtergrond_KP/MapServer/WMTS/1.0.0/WMTSCapabilities.xml')
      fetch('https://cartoweb.wmts.ngi.be/1.0.0/WMTSCapabilities.xml')
      .then(function (response) {
        return response.text();
      })
      .then(function (text) {
        const result = parser.read(text);
        const options = optionsFromCapabilities(result, {
          layer: 'topo_grey',
          projection: 'EPSG:3857',
        });

        const backgroundLayer = new TileLayer({opacity: 0.7, source: new WMTS(options)});  //new TileLayer({source: new OSM()});
        map = new Map({
          target,
          view,
          layers: [backgroundLayer, dataLayer],
          overlays: [popup]
        });
        // popup
        map.addInteraction(select);
        // legend
        let legend = new Legend({
          style: styleFunction,
          margin: 2,
          size: [35, 35]
        });
        legend.addItem({ title:'Fantastisch proper', properties: { "klasse": 'Fantastisch proper' }, typeGeom: 'Point'});
        legend.addItem({ title:'Heel goed', properties: { "klasse": 'Heel goed'}, typeGeom: 'Point' });
        legend.addItem({ title:'Ai, dat kan beter', properties: { "klasse": 'Ai, dat kan beter' }, typeGeom: 'Point' });
        legend.addItem({ title:'Vuil', properties: { "klasse": 'Vuil' }, typeGeom: 'Point' });
        map.addControl(new ol_control_Legend ({
          collapsible: false,
          legend: legend
        }));

      })
  }});
